import React from 'react';
import apiClient from '../services/api';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import NewTask from './NewTask';
import NewIssue from './NewIssue';
import Replies from './Replies';
export default class Issue extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            issues: [],
            replies:[],
        };
    }


    fetchIssues() {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie').then(() => apiClient.get('/api/issues')
                .then(response => {
                    const issues = response.data;
                    this.setState({ issues: issues });

                })
                .catch(error => console.error(error)
                ))

        }
    }

    fetchReplies(id) {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie').then(() => apiClient.post('/api/getReplies',{id:id})
                .then(response => {
                    const replies = response.data;
                    this.setState({ replies: replies });

                })
                .catch(error => console.error(error)
                ))

        }
    }

    componentDidMount() {

        this.fetchIssues();

    }


    render() {

        return (<>

            <div><NewIssue /></div>
            <div classsName="row">
                {this.state.issues.map((issues) => {
                    return (<div className="shadow">
                    <div><h3>{issues.user.name}:</h3></div>
                       <div><h3>{issues.title}</h3></div>
                       <div><p>{issues.issue}</p></div>
                       <button onClick={(e)=>{this.fetchReplies(issues.id),console.log(this.state.replies)}}>Show Replies</button>

                       {/* <div>{issues.reply.map((replies)=>{return(<><div>{replies.user_reply.name}</div><div>{replies.reply}</div></>);})}</div> */}
                 <Replies replies={this.state.replies}/> </div>  );


                })}

            </div>
        </>

        )
    }
}
