import React from 'react';
import apiClient from '../services/api';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import NewTask from './NewTask';
import NewIssue from './NewIssue';
import Replies from './Replies';

export default class IssueDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            issues: [],
            replies: [],
        };
    }

    fetchIssues() {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie').then(() => apiClient.post('/api/getIssueByID', { id: 1 })
                .then(response => {
                    const issues = response.data;
                    this.setState({ issues: issues });

                })
                .catch(error => console.error(error)
                ))

        }
    }

    fetchReplies() {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie').then(() => apiClient.post('/api/getReplies', { id: this.props.id })
                .then(response => {
                    const replies = response.data;
                    this.setState({ replies: replies });

                })
                .catch(error => console.error(error)
                ))

        }
    }

    componentDidMount() {

        this.fetchIssues();
        this.fetchReplies();
    }




    render() {

        return (<>
            <div> <div className="shadow" >
                {/* <div><h3>{this.state.issues.user.name}:</h3></div> */}
                <div><h3>{this.state.issues.title}</h3></div>
                <div><p>{this.state.issues.issue}</p></div> {this.state.replies.map((replies) => {
                    return(
                         <div key={replies.id}> <h3>{replies.user.name}</h3><p>{replies.reply}</p></div> )})}</div>
            </div>
        </>

        )
    }
}
